mapper.select:
SELECT sys_device.device_id, sys_device.device_code, sys_device.device_address, sys_device.device_detailed_address, sys_device.offline_admin, sys_device.admin_phone_num, sys_device.`status`, sys_device.position, sys_device.create_date, sys_user.user_id, sys_user.nickname FROM sys_device LEFT JOIN sys_user_dev ON sys_user_dev.device_id = sys_device.device_id LEFT JOIN sys_user ON sys_user_dev.user_id = sys_user.user_id
and a.device_code like concat('%',#{deviceCode},'%')
and c.nickName like concat('%',#{nickName},'%')
and a.device_address like concat('%',#{deviceAddress},'%')
and a.'status' like concat('%',#{status},'%')
and a.'creaDate' between #{startTime} and #{endTime}
order by ${sidx} ${order}
order by sys_device.device_id desc
limit #{offset}, #{limit}

SELECT count(*) FROM sys_device LEFT JOIN sys_user_dev ON sys_user_dev.device_id = sys_device.device_id LEFT JOIN sys_user ON sys_user_dev.user_id = sys_user.user_id
and a.device_code like concat('%',#{deviceCode},'%')
and c.nickName like concat('%',#{nickName},'%')
and a.device_address like concat('%',#{deviceAddress},'%')
and a.'status' like concat('%',#{status},'%')
and a.'creaDate' between #{startTime} and #{endTime}
order by ${sidx} ${order}
order by sys_device.device_id desc
limit #{offset}, #{limit}
